import React from 'react';
import ReactDOM from 'react-dom';
import Files from "react-files";
import './index.css';

class Table extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Instructions: this.props.value
            //result: []
        }
    }
    
    renderTableData(result) {
            return result.map((move, index) => {
                const{ step, roombaLocation, action, ttlDirtCollected, ttlWallHits} = move;
                return(
                    <tr key={step}>
                        <td>{step+1}</td>
                        <td>{roombaLocation}</td>
                        <td>{action}</td>
                        <td>{ttlDirtCollected}</td>
                        <td>{ttlWallHits}</td>
                    </tr>
                )
            })
    }
    
    render() {
        console.log(this.state.Instructions);
        var roomDimensions= this.state.Instructions.roomDimensions;
        var initialRoombaLocation= this.state.Instructions.initialRoombaLocation;
        var dirtLocations= this.state.Instructions.dirtLocations;
        var drivingInstructions = this.state.Instructions.drivingInstructions;
        var result = [];
            
        const eastWall = roomDimensions[0];
        const southWall = roomDimensions[1];
        var currRoombaLocation = initialRoombaLocation;
        var wallHits = 0;
        var dirtCollected = 0;
        var listOfDirtCollected = [];
        
        var initialIndexOfDirt = dirtLocations.findIndex(index => index[0] === currRoombaLocation[0] && index[1] === currRoombaLocation[1]) //check if start on dirt
            if(initialIndexOfDirt !== -1){
                dirtCollected++;
                listOfDirtCollected.push(dirtLocations.splice(initialIndexOfDirt,1));
        }
        
        result.push([{step: 0, roombaLocation: initialRoombaLocation, action: "", ttlDirtCollected: dirtCollected, ttlWallHits: 0}]); //create the initial row
        console.log(result);
        
        for(var i = 0; i<drivingInstructions.length; i++){
            
            var currentInstruction = drivingInstructions[i].toUpperCase();
            var nextLocation = currRoombaLocation;
            switch(currentInstruction) {
                case "N":
                    nextLocation[1]--;
                    break;
                case "S":
                    nextLocation[1]++;
                    break;
                case "W":
                    nextLocation[0]--;
                    break;
                case "E":
                    nextLocation[0]++;
                    break;
                default:
                    console.log("Invalid Direction");
            }
            
            if(nextLocation[0]<0 || nextLocation[0] >= eastWall || nextLocation[1]<0 || nextLocation[1] >= southWall){
                nextLocation = currRoombaLocation;
                wallHits ++;
            }
            currRoombaLocation = nextLocation;
            
            var indexOfDirt = dirtLocations.findIndex(index => index[0] === currRoombaLocation[0] && index[1] === currRoombaLocation[1])
            if(indexOfDirt !== -1){
                dirtCollected++;
                listOfDirtCollected.push(dirtLocations.splice(indexOfDirt,1));
            }
            
            result.push({step: i+1, roombaLocation: currRoombaLocation, action: currentInstruction, ttlDirtCollected: dirtCollected, ttlWallHits: wallHits});
            console.log(result);
        }
        return (
            <div>
                <table id ='steps'>
                    <tbody>
                        {this.renderTableData(result)}
                    </tbody>
                </table>
            </div>
        )
    
    }
}

class JSONButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            jsonFile: {},
            fileSent: false
            
        };

        this.fileReader = new FileReader();
        this.fileReader.onload = event => {
        this.setState({ jsonFile: JSON.parse(event.target.result) }, () => {
        console.log(this.state.jsonFile);
        this.setState({fileSent: true});
      });
    };
  }

    /*handleInput() {
        this.setState({fileSent: true})
    }*/
  renderTable() {
      return <Table value={this.state.jsonFile} />;
  }
    
  render() {
    return (
    <React.Fragment>
      <div className="files">
        <Files
          className="files-dropzone"
          onChange={file => {
            this.fileReader.readAsText(file[0]);
          }}
          onError={err => console.log(err)}
          accepts={[".json"]}
          multiple
          maxFiles={1}
          maxFileSize={10000000}
          minFileSize={0}
          clickable
        >
          Drop files here or click to upload
        </Files>
      </div>
    {
        this.state.fileSent?
        <div className ="Table">
            {this.renderTable()}
        </div>
        :
        <div></div>
    }
    </React.Fragment>
    );
  }
}

// ==================================
ReactDOM.render(
    <JSONButton />, document.getElementById('root')
);
