import React from 'react';
import ReactDOM from 'react-dom';
import Files from "react-files";
import './index.css';

class Table extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Instructions: this.props.data,
            
            result: []
        }
    }
    
    renderTableData() {
            return this.state.result.map((move, index) => {
                const{ step, roombaLocation, action, ttlDirtCollected, ttlWallHits} = move;
                return(
                    <tr key={step}>
                        <td>{step}</td>
                        <td>{roombaLocation}</td>
                        <td>{action}</td>
                        <td>{ttlDirtCollected}</td>
                        <td>{ttlWallHits}</td>
                    </tr>
                )
            })
    }
    
    render() {
        const eastWall = this.state.roomDimensions[0];
        const southWall = this.state.roomDimensions[1];
        var currRoombaLocation = this.state.initialRoombaLocation;
        var wallHits = 0;
        var dirtCollected = 0;
        var listOfDirtCollected = [];
        
        var initialIndexOfDirt = this.state.dirtLocations.findIndex(index => index[0] === currRoombaLocation[0] && index[1] === currRoombaLocation[1]) //check if start on dirt
            if(initialIndexOfDirt !== -1){
                dirtCollected++;
                listOfDirtCollected.push(this.state.dirtLocations.splice(initialIndexOfDirt,1));
        }
        
        this.state.result.push([{step: 1, roombaLocation: this.state.initialRoombaLocation, action: "", ttlDirtCollected: dirtCollected, ttlWallHits: 0}]); //create the initial row
        
        for(var i = 0; i<this.state.drivingInstructions.length; i++){
            
            var currentInstruction = String.toUpperCase(this.state.drivingInstructions[i]);
            var nextLocation = currRoombaLocation;
            switch(currentInstruction) {
                case "N":
                    nextLocation[1]--;
                    break;
                case "S":
                    nextLocation[1]++;
                    break;
                case "W":
                    nextLocation[0]--;
                    break;
                case "E":
                    nextLocation[0]++;
                    break;
                default:
                    console.log("Invalid Direction");
            }
            
            if(nextLocation[0]<0 || nextLocation[0] >= eastWall || nextLocation[1]<0 || nextLocation[1] >= southWall){
                nextLocation = currRoombaLocation;
                wallHits ++;
            }
            currRoombaLocation = nextLocation;
            
            var indexOfDirt = this.state.dirtLocations.findIndex(index => index[0] === currRoombaLocation[0] && index[1] === currRoombaLocation[1])
            if(indexOfDirt !== -1){
                dirtCollected++;
                listOfDirtCollected.push(this.state.dirtLocations.splice(indexOfDirt,1));
            }
            
            this.state.result.push({step: i, roombaLocation: currRoombaLocation, action: currentInstruction, ttlDirtCollected: dirtCollected, ttlWallHits: wallHits});
        }
        return (
            <div>
                <table id ='steps'>
                    <tbody>
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        )
    }
}

class JSONButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        jsonFile: {}
        };

        this.fileReader = new FileReader();
        this.fileReader.onload = event => {
        this.setState({ jsonFile: JSON.parse(event.target.result) }, () => {
        console.log(this.state.jsonFile);
      });
    };
  }

  renderTable() {
        return <Table value={this.state.jsonFile} />;
    }
  render() {
    return (
        <React.Fragment>
      <div className="files">
        <Files
          className="files-dropzone"
          onChange={file => {
            this.fileReader.readAsText(file[0]);
          }}
          onError={err => console.log(err)}
          accepts={[".json"]}
          multiple
          maxFiles={1}
          maxFileSize={10000000}
          minFileSize={0}
          clickable
        >
          Drop files here or click to upload
        </Files>
      </div>
      <div className="Table">
            {this.renderTable()}
      </div>
    </React.Fragment>
    );
  }
}

// ==================================
ReactDOM.render(
    <JSONButton />, document.getElementById('root')
);
